import React,{useState} from 'react';
import { Route} from "react-router-dom";
import API from "./utils/API";
import logo from './logo.svg';
import './App.css';
import bookFunctions from './utils/API';
import SavedPage from './components/SavedPage'
import SearchPage from './components/SearchPage'

function App() {

  var [state, setstate] = useState({
    search: "",
    results:[]

  });

  var handleTyping = (e) => {

    setstate({
      ...state,search: e.target.value
    }) 

  } 

  var handleClick = () => {
    API.search(state.search,function(data){
      setstate({
        ...state,results: data.data.items
      })

    })
    console.log("yougot clicked")
  }
  console.log(state)
  return (
    <div className="App">
      <input onChange={handleTyping}/>
      <button onClick={handleClick}>Search</button>
    {state.results.map(function(book){
      return (

          <div>
            <h1>{book.volumeInfo.title}</h1>
            <h1>{book.volumeInfo.author}</h1>
            <p>{book.volumeInfo.description}</p>
            <img src={book.volumeInfo.imageLinks.thumbnail}/>
            <a href={book.volumeInfo.infoLink}>More Info</a>

            <Route exact path={"/"}>
          <SearchPage />
        </Route>
        <Route exact path={"/saved"}>
          <SavedPage />
        </Route>
          </div>

      )

    })}


    </div>
  );
}

export default App;

// https://www.googleapis.com/books/v1/volumes?q=
