import React,{useState} from 'react';
import API from '../../utils/API'
function Search() {
   
    var [state, setstate] = useState({
        search: "",
        results: []

    });

    var handleTyping = (e) => {

        setstate({
            ...state, search: e.target.value
        })

    }

    var handleClick = () => {
        API.search(state.search, function (data) {
            setstate({
                ...state, results: data.data.items
            })

        })
        console.log("yougot clicked")
    }
    const makeBook = (event) => {
        let bookIndex = parseInt(event.target.getAttribute('index'));
        axios.post('/api/books', searchResults[bookIndex]);
    }
    console.log(state)
    return (

        <div className="App">
            <input onChange={handleTyping} />
            <button onClick={handleClick}>Search</button>
            {state.results.map(function (book) {
                return (

                    <div>
                        <h1>{book.volumeInfo.title}</h1>
                        <h1>{book.volumeInfo.author}</h1>
                        <p>{book.volumeInfo.description}</p>
                        <img src={book.volumeInfo.imageLinks.thumbnail} />
                        <a href={book.volumeInfo.infoLink}>More Info</a>


                        searchResults.map((result, index) => <BookItem key={index} index={index} buttonText="Add" buttonClicked={addBook} book={result} />)}
                    </div>


                )

            })}


        </div>
    );
}
export default Search;
