import React, { useEffect, useState } from 'react'
import axios from 'axios'
import API from "../utils/API";

function SavedPage() {

    const [books, setBooks] = useState([])
    
    useEffect(() => {
      loadBooks()
    }, [])
  
   
    function loadBooks() {
      API.getBooks()
        .then(res =>
          setBooks(res.data)
        )
        .catch(err => console.log(err));
    };
  
    
    function deleteBook(id) {
      API.deleteBook(id)
        .then(res => loadBooks())
        .catch(err => console.log(err));
    }
    return (
        <div className="container">
            {state.results.map(function (book) {
                return (

                    <div>
                        <h1>{book.volumeInfo.title}</h1>
                        <h1>{book.volumeInfo.author}</h1>
                        <p>{book.volumeInfo.description}</p>
                        <img src={book.volumeInfo.imageLinks.thumbnail} />
                        <a href={book.volumeInfo.infoLink}>More Info</a>



                    </div>


                )

            })}





                        <div className="card-body">
                        <button type="button" className="btn btn-primary" onClick={() => deleteBook(book._id)}>Delete  <i className="far fa-trash-alt"></i></button>
                        </div>
                    
        </div>
    );
}
export default SavedPage;
