import React, { useEffect, useState } from 'react';
import axios from 'axios';
import API from "../../utils/API" ;

function SavedPage() {

    const [books, setBooks] = useState([])
    
    useEffect(() => {
      loadBooks()
    }, [])
  
    function deleteBook(id) {
        API.deleteBook(id)
          .then(res => loadBooks())
          .catch(err => console.log(err));
      }

    function loadBooks() {
      API.getBooks()
        .then(res =>
          setBooks(res.data)
        )
        .catch(err => console.log(err));
    };
  
    
    
    return (
        
         
           
        <div className="card-body">
        {books.map((book, index) => <div key={index} index={index} buttonText="Remove" buttonClicked={deleteBook} book={book}/>)}
    </div>
      );
    }
    
export default SavedPage;
